import React, { useEffect, useState } from 'react';
import { MDBContainer } from 'mdb-react-ui-kit';
import {
  MDBCarousel,
  MDBCarouselItem,
  MDBCard,
  MDBCardBody,
  MDBCardTitle,
  MDBCardText,
  MDBCardImage,
  MDBBtn
} from 'mdb-react-ui-kit';
import axios from 'axios';

function App() {
  const [banners, setBanners] = useState(null);
  useEffect(()=>{
    axios.get('https://photo.azurecloud.vn/api/v1/banners/list-banner-active')
    .then(json =>{
            let b = json.data.data.banners.data;
            setBanners(b);
    })
  },[]);
  return (
    <div>
      <div>
     
        
        
        
        <MDBCarousel showIndicators showControls fade>

      {
          banners.map(banner =>(
                <MDBCarouselItem
                          className={banner.id==1?'w-100 d-block':'w-100 d-block active'}
                          itemId={banner.id}
                          src={banner.image}
                          alt='...'
                          key={banner.id}
                  >
                     </MDBCarouselItem>
          ))
        }
    </MDBCarousel>
      </div>
      
     
    </div>
      
      

  );
}

export default App;
