import * as React from 'react';

import { css, CSSAttribute } from 'goober';

export interface StarProps {
  allowHalf?: boolean;
  classNamePrefix?: string;
  count?: number;
  disabled?: boolean;
  index?: number;
  onClick?: (e: React.MouseEvent<HTMLLIElement>, index: number) => void;
  onHover?: (e: React.MouseEvent<HTMLLIElement>, index: number) => void;
  reverse?: boolean;
  style?: { full?: CSSAttribute; half?: CSSAttribute; zero?: CSSAttribute };
  styleFull?: { first?: CSSAttribute; second?: CSSAttribute };
  styleFullHover?: { first?: CSSAttribute; second?: CSSAttribute };
  styleHalf?: { first?: CSSAttribute; second?: CSSAttribute };
  styleHalfHover?: { first?: CSSAttribute; second?: CSSAttribute };
  styleHover?: {
    full?: CSSAttribute;
    half?: CSSAttribute;
    zero?: CSSAttribute;
  };
  styleZero?: { first?: CSSAttribute; second?: CSSAttribute };
  styleZeroHover?: { first?: CSSAttribute; second?: CSSAttribute };
  symbol?: React.ReactNode;
  value?: number;
}

export class Star extends React.Component<StarProps> {
  starRef?: HTMLLIElement | null;

  onClick: React.MouseEventHandler<HTMLLIElement> = e => {
    const { index, onClick } = this.props;
    onClick!(e, index!);
  };

  onHover: React.MouseEventHandler<HTMLLIElement> = e => {
    const { index, onHover } = this.props;
    onHover!(e, index!);
  };

  getStarType = () => {
    const { allowHalf, index, value } = this.props;
    const starValue = index! + 1;

    if (allowHalf && value! + 0.5 >= starValue && value! < starValue) {
      return 'half';
    } else if (starValue <= value!) {
      return 'full';
    }

    return 'zero';
  };

  getValue = (x: number, reverse: boolean) => {
    const { allowHalf, index } = this.props;
    let value = index! + 1;
    if (allowHalf) {
      const doc = this.starRef!.ownerDocument;
      const { body } = doc!;
      const docElem = doc.documentElement;
      const box = this.starRef?.getBoundingClientRect();
      let left = box?.left;
      left! -= docElem?.clientLeft || body.clientLeft || 0;
      const w: Window = doc.defaultView || (doc as any).parentWindow;
      let ret = w.pageXOffset;
      const method = 'scrollLeft';
      if (typeof ret !== 'number') {
        ret = doc.documentElement[method];
        if (typeof ret !== 'number') {
          ret = doc.body[method];
        }
      }
      left! += ret;
      const width = this.starRef?.clientWidth;
      if (!reverse && x - left! < width! / 2) {
        value -= 0.5;
      } else if (reverse && x - left! > width! / 2) {
        value -= 0.5;
      }
    }

    return value;
  };

  render() {
    const {
      classNamePrefix,
      count,
      disabled,
      index,
      reverse,
      style,
      styleFull,
      styleFullHover,
      styleHalf,
      styleHalfHover,
      styleHover,
      styleZero,
      styleZeroHover,
      symbol,
      value,
    } = this.props;

    const symbolNode =
      typeof symbol === 'function' ? symbol(this.props) : symbol;

    const starType = this.getStarType();

    const classNameMap = {
      zero: `${classNamePrefix}__star--zero`,
      half: `${classNamePrefix}__star--half`,
      full: `${classNamePrefix}__star--full`,
    };
    const className = `${classNamePrefix}__star ${classNameMap[starType]}`;

    const styleMap = { zero: styleZero, full: styleFull, half: styleHalf };
    const styleHoverMap = {
      zero: styleZeroHover,
      full: styleFullHover,
      half: styleHalfHover,
    };

    const FirstStarStyles = css`
      position: absolute;
      left: 0;
      top: 0;
      width: 50%;
      height: 100%;
      overflow: hidden;
      opacity: 0;
      transition: all 500ms ease;
    `;

    const FirstStarHoverStyles = css({
      '&:hover': styleHoverMap[starType]?.first,
    });

    const SecondStarHoverStyles = css({
      '&:hover': styleHoverMap[starType]?.second,
    });

    const StarActiveStyles = {
      zero: '',
      half: css`
        .${FirstStarStyles} {
          opacity: 1;
          color: #ffd166;
        }

        &:hover {
          .${FirstStarStyles} {
            color: #ffe3a2;
          }
        }
      `,
      full: css`
        color: #ffd166 !important;

        &:hover {
          color: #ffe3a2;
        }
      `,
    };

    const StarStyles = css`
      margin: 0;
      padding: 0;
      display: inline-block;
      margin-right: 8px;
      position: relative;
      color: #e8eaeb;
      cursor: pointer;
      transition: all 500ms ease;
      ${reverse
        ? `
        margin-right: 0;
        margin-left: 8px;
        float: right;

        .${FirstStarStyles} {
            right: 0;
            left: auto;
        }
      `
        : ``}
    `;

    const StarHoverStyles = css({
      '&:hover': styleHover![starType],
    });

    return (
      <li
        aria-checked={value! > index! ? 'true' : 'false'}
        aria-label={`${index! + 1} out of ${count} stars`}
        aria-posinset={index! + 1}
        aria-setsize={count}
        className={`${className} ${StarStyles} ${StarHoverStyles} ${StarActiveStyles[starType]}`}
        onClick={disabled ? undefined : this.onClick}
        onMouseMove={disabled ? undefined : this.onHover}
        ref={ref => {
          this.starRef = ref;
        }}
        role="radio"
        style={style![starType]}
        tabIndex={disabled ? -1 : 0}
      >
        <div
          aria-hidden="true"
          className={`${classNamePrefix}__star__first ${FirstStarStyles} ${FirstStarHoverStyles}`}
          style={styleMap[starType]?.first}
        >
          {symbolNode}
        </div>
        <div
          aria-hidden="true"
          className={`${classNamePrefix}__star__second ${SecondStarHoverStyles}`}
          style={styleMap[starType]?.second}
        >
          {symbolNode}
        </div>
      </li>
    );
  }
}

export default Star;
import * as React from 'react';

import { setup, css, CSSAttribute } from 'goober';
import { prefix } from 'goober/prefixer';

import Star from './star';

setup(React.createElement, prefix);

const noop = () => {};

export interface StarsRatingProps {
  allowClear?: boolean;
  allowHalf?: boolean;
  autoFocus?: boolean;
  classNamePrefix?: string;
  count?: number;
  defaultValue?: number;
  direction?: 'ltr' | 'rtl';
  disabled?: boolean;
  onBlur?: () => void;
  onChange?: (value?: number) => void;
  onFocus?: () => void;
  onHoverChange?: (value?: number) => void;
  style?: StarsRatingStyles;
  symbol?: React.ReactNode;
  tabIndex?: number;
  value?: number;
}

type StarsRatingStyles = {
  style?: CSSAttribute;
  full?: {
    first?: CSSAttribute;
    second?: CSSAttribute;
    star?: CSSAttribute;
  };
  half?: {
    first?: CSSAttribute;
    second?: CSSAttribute;
    star?: CSSAttribute;
  };
  zero?: {
    first?: CSSAttribute;
    second?: CSSAttribute;
    star?: CSSAttribute;
  };
  hover?: Omit<StarsRatingStyles, 'hover'>;
};

interface StarsRatingState {
  clearedValue?: number;
  hoverValue?: number;
  value?: number;
}

class StarsRating extends React.Component<StarsRatingProps, StarsRatingState> {
  static defaultProps = {
    allowClear: true,
    allowHalf: true,
    classNamePrefix: 'react-star-rate',
    count: 5,
    defaultValue: 0,
    direction: 'ltr',
    disabled: false,
    onChange: noop,
    onHoverChange: noop,
    style: {},
    symbol: '★',
    tabIndex: 0,
  };

  ratingContainerRef?: HTMLUListElement | null;

  starRefs?: { [key: number]: Star };

  constructor(props: StarsRatingProps) {
    super(props);

    const { defaultValue, value } = props;

    this.starRefs = {};

    this.state = {
      value: value ?? defaultValue,
    };
  }

  componentDidMount() {
    const { autoFocus, disabled } = this.props;
    if (autoFocus && !disabled) {
      this.focus();
    }
  }

  static getDerivedStateFromProps(
    nextProps: StarsRatingProps,
    state: StarsRatingState
  ) {
    if ('value' in nextProps && nextProps.value !== undefined) {
      return {
        ...state,
        value: nextProps.value,
      };
    }
    return state;
  }

  onHover = (event: React.MouseEvent<HTMLLIElement>, index: number) => {
    const { onHoverChange } = this.props;
    const { clearedValue } = this.state;
    const hoverValue = this.getStarValue(index, event.pageX);
    if (hoverValue !== clearedValue) {
      this.setState({
        hoverValue,
        clearedValue: undefined,
      });
    }
    onHoverChange!(hoverValue);
  };

  onMouseLeave = () => {
    const { onHoverChange } = this.props;
    this.setState({
      hoverValue: undefined,
      clearedValue: undefined,
    });
    onHoverChange!(undefined);
  };

  onClick = (event: React.MouseEvent, index: number) => {
    const { allowClear } = this.props;
    const { value } = this.state;
    const newValue = this.getStarValue(index, event.pageX);
    const isClear = allowClear ? newValue === value : false;
    this.onMouseLeave();
    this.changeValue(isClear ? 0 : newValue);
    this.setState({
      clearedValue: isClear ? newValue : undefined,
    });
  };

  onFocus = () => {
    const { onFocus } = this.props;
    onFocus && onFocus();
  };

  onBlur = () => {
    const { onBlur } = this.props;
    onBlur && onBlur();
  };

  changeValue = (value: number) => {
    const { onChange } = this.props;
    if (!('value' in this.props)) {
      this.setState({
        value,
      });
    }
    onChange!(value);
  };

  saveStarRef = (index: number) => (node: Star) => {
    this.starRefs![index] = node;
  };

  getStarValue = (index: number, x: number) => {
    const { direction } = this.props;
    const reverse = direction === 'rtl';
    return this.starRefs![index].getValue(x, reverse);
  };

  focus = () => {
    const { disabled } = this.props;
    if (!disabled) {
      this.ratingContainerRef?.focus();
    }
  };

  render() {
    const {
      allowHalf,
      classNamePrefix,
      count,
      direction,
      disabled,
      style,
      symbol,
      tabIndex,
    } = this.props;
    const { hoverValue, value } = this.state;

    const stars = [];
    for (let index = 0; index < (count ?? 0); index++) {
      stars.push(
        <Star
          allowHalf={allowHalf}
          classNamePrefix={classNamePrefix}
          count={count}
          disabled={disabled}
          index={index}
          key={index}
          ref={this.saveStarRef(index)}
          onClick={this.onClick}
          onHover={this.onHover}
          reverse={direction === 'rtl'}
          style={{
            full: style?.full?.star,
            half: style?.half?.star,
            zero: style?.zero?.star,
          }}
          styleFull={style?.full}
          styleFullHover={style?.hover?.full}
          styleHalf={style?.half}
          styleHalfHover={style?.hover?.half}
          styleHover={{
            full: style?.hover?.full?.star,
            half: style?.hover?.half?.star,
            zero: style?.hover?.zero?.star,
          }}
          styleZero={style?.zero}
          styleZeroHover={style?.hover?.zero}
          symbol={symbol}
          value={hoverValue === undefined ? value : hoverValue}
        />
      );
    }

    const ListContainerStyles = css`
      margin: 0;
      padding: 0;
      list-style: none;
      font-size: 40px;
      display: inline-block;
      vertical-align: middle;
      font-weight: normal;
      font-style: normal;
      font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen,
        Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
    `;

    const ListContainerHoverStyles = css({
      '&:hover': style?.hover?.style,
    });

    return (
      <ul
        aria-label="Stars Rating"
        className={`${classNamePrefix} ${ListContainerStyles} ${ListContainerHoverStyles} ${
          direction === 'rtl'
            ? `${classNamePrefix}--rtl ${css`
                direction: rtl;
              `}`
            : `${classNamePrefix}--ltr ${css`
                direction: ltr;
              `}`
        }`}
        onBlur={this.onBlur}
        onFocus={this.onFocus}
        onMouseLeave={disabled ? undefined : this.onMouseLeave}
        ref={ref => {
          this.ratingContainerRef = ref;
        }}
        role="radiogroup"
        style={style?.style}
        tabIndex={disabled ? -1 : tabIndex}
      >
        {stars}
      </ul>
    );
  }
}

export default StarsRating;
