import React, { useEffect, useState } from 'react';
import { MDBContainer } from 'mdb-react-ui-kit';
import {
  MDBCarousel,
  MDBCarouselItem,
  MDBCard,
  MDBCardBody,
  MDBCardTitle,
  MDBCardText,
  MDBCardImage,
  MDBBtn
} from 'mdb-react-ui-kit';
import { Slide } from 'react-slideshow-image';
import 'react-slideshow-image/dist/styles.css';
import './index1.css';
import axios from 'axios';

function App() {
  const [banners, setBanners] = useState([]);
  useEffect(()=>{
    axios.get('https://photo.azurecloud.vn/api/v1/banners/list-banner-active')
    .then(json =>{
            let b = json.data.data.banners.data;
            setBanners(b);
    })
  },[]);
  return (
    <div className="slide-container">
        <Slide>
         {banners.map((slideImage, index)=> (
            <div className="each-slide" key={index}>
              <div style={{'backgroundImage': `url(${slideImage.image})`}}>
                <span>{slideImage.title}</span>
              </div>
            </div>
          ))} 
        </Slide>
      </div>
      
      

  );
}

export default App;
